const INITIAL_STATE = {
  personal: {
    name: '',
    email: '',
    cpf: '',
    address: '',
    city: '',
    uf: '',
  },
  professional: {
    curriculum: '',
    job: '',
    description: '',
  },
};

export const profileReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
  case 'PERSONAL_FORM':
    return {
      ...state,
      personal: { ...action.payload },
    };
  case 'PROFESSIONAL_FORM':
    return {
      ...state,
      professional: { ...action.payload },
    };
  default:
    return state;
  }
};

export default profileReducer;
